function confirmEnding(str, target) {
  // "Never give up and good luck will find you."
  // -- Falcor
  let targetLen = target.length;
  let subStr = str.substring(str.length-targetLen) ;
  if(subStr === target){
    return true;
  }
  else{
    return false;
  }


}

confirmEnding("Bastian", "n");
