function mutation(arr) {
  let bol;
  let a1 = arr[0].toLowerCase();
  let a2 = arr[1].toLowerCase();
for(let i =0;i<a2.length;i++){
  if((a1.indexOf(a2[i]))=== -1){
    bol = false;
    break;
  }
  else{
    bol = true;
  }
}
  return bol;
}

