function titleCase(str) {

let spt = str.split(" ");
let newArr = [];

  for(let i = 0;i < spt.length;i++){

    let l = spt[i][0].toUpperCase();
    for(let j = 1;j < spt[i].length;j++){
      l+=spt[i][j].toLowerCase();
    }
    newArr.push(l);
  }

  return newArr.join(" ");
}
