function titleCase(str) {
  let spt = str.split(" ");
  let newArr= "";
  spt = spt.map((n)=> {
  let l ="";
  for(let i=1;i<n.length;i++){
    l+= n[i].toLowerCase();
  }
  return (n[0].toUpperCase() +l );
});

for(let j=0;j<spt.length;j++){
newArr = newArr + spt[j] + " ";
}
  return newArr.trim();
}

