function getIndexToIns(arr, num) {
  // Find my place in this sorted array.
  arr.push(num);
  let pos;
  let value;
  for(let i = 1;i<arr.length;i++){
    pos = i;
    value = arr[i];
    while(pos > 0 && value < arr[pos-1]){
      arr[pos] = arr[pos-1];
      pos = pos-1;
    }
    arr[pos] = value;
  }

  return arr.indexOf(num);
}

